import 'package:flutter/material.dart';
import 'package:flutter_barcode_scanner/flutter_barcode_scanner.dart';
import 'package:smart_box/baggage/Item.dart';
import 'package:smart_box/baggage/box.dart';
import "package:smart_box/barcode_reader/BarcodeReader.dart";
import 'package:smart_box/ui/ItemListWidget.dart';

///
/// バーコード読み込みテスト用Widget
///
class Camera extends StatefulWidget {
  @override
  _CameraState createState() => _CameraState();
}

class _CameraState extends State<Camera> {
  String _scanResult = '';
  Box aBox;

  @override
  void initState() {
    super.initState();
    String boxName = "QRテストボックス";
    List<Item> itemList = [];
    for (int j = 0; j < 10; j++) {
      itemList.add(Item(j, boxName + "アイテム" + j.toString(),
          base64Image:
              ""));
    }
    scanBarcode("#ff6666", ScanMode.QR).then((result) {

      setState(() {
        this.aBox = Box(1, boxName, items: itemList);
      });
    });
  }

  Future<bool> _onWillPopHandler() async {
    return false;
  }

  @override
  Widget build(BuildContext context) {
    return WillPopScope(
        child: Scaffold(
          appBar: AppBar(
            title: Text("アイテム一覧"),
            leading: IconButton(
              icon: Icon(Icons.arrow_back_ios),
              onPressed: _onWillPopHandler,
            ),
            actions: [
              Container(
                  margin: EdgeInsets.fromLTRB(0, 0, 20, 0),
                  child: IconButton(
                    icon: Icon(
                      Icons.search,
                      size: 40,
                      color: Theme.of(context).accentColor,
                    ),
                    onPressed: () {
                      print("search button pushed");
                    },
                  ))
            ],
          ),
          body: (this.aBox == null) ? Container() : ItemListWidget(this.aBox),
        ),
        onWillPop: this._onWillPopHandler);
  }
}
